export const handleToList=handles=>{if(handles instanceof Array){return handles;}return [handles];};
export const compareVersion=(version1,version2)=>{if(version1===version2){return 0;}const a_components=version1.split('.');const b_components=version2.split('.');const len=Math.min(a_components.length,b_components.length);for(let i=0;i<len;i++){if(parseInt(a_components[i])>parseInt(b_components[i])){return 1;}if(parseInt(a_components[i])<parseInt(b_components[i])){return -1;}}if(a_components.length>b_components.length){return 1;}if(a_components.length<b_components.length){return -1;}return 0;};
